import {View, FlatList, Animated} from 'react-native';
import React from 'react'

import globalStyles from '../../../globalStyles'
import styles from '../feed/styles'
import SearchBar from '../../../assets/components/searchBar'
import EventCard from '../../../assets/components/eventCard'

export default class Feed extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        userName: "KIRBY",
        courseName: "Ciência da Computação",
        eventData: [
          {
            eventType: "Nome da Palestra",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundoRevolução da IA, Mudanças no mundoRevolução da IA, Mudanças no mundoMudanças no mundoRevolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 1",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundRevoluçãoRevoluçãoRevoluçãoRevoluçãoRevoluçãoRevoluçãoRevoluçãoo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 2",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 3",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 4",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 5",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
          {
            eventType: "Palestra 6",
            eventDate: "16/10 - 10:00",
            eventName: "Revolução da IA, Mudanças no mundo",
            eventLocal: "Auditório Principal - Prédio A",
          },
        ]
    }
  }
  
  render(){
    return(
      <View style = {globalStyles.container}>
        <SearchBar
          userName={this.state.userName}
          courseName={this.state.courseName}
        ></SearchBar>
        <View style={styles.eventsContainer}>
          <FlatList
            contentContainerStyle={styles.cardContainer}
            data={this.state.eventData}
            renderItem={({ item }) => (
              <EventCard
                eventType={item.eventType}
                eventDate={item.eventDate}
                eventName={item.eventName}
                eventLocal={item.eventLocal}>
              </EventCard>
            )}
          />
        </View>
      </View>
    );
  }
}
